-- Create Companies table
CREATE TABLE Companies (
    EC INT,                                     -- Employee count
    F DATE,                                     -- Founded
    HQ VARCHAR(255),                            -- Headquarters
    CEO VARCHAR(255),                           -- 
    EH VARCHAR(255),                            -- Earnings History
    SYM VARCHAR(255),                           -- 
    [Name] VARCHAR(255),                        --
    Id INT PRIMARY KEY,                         --
    Industry VARCHAR(255),                      --
    [Desc] NVARCHAR(MAX),                       --
    [Price] DECIMAL(10, 2)                      --
);



